import React, {useCallback, useState, useEffect} from 'react'
import * as pyodide from 'pyodide';
import * as pandas from 'pandas'
import fs from 'fs';
pyodide.loadPackage('pandas').then(() => {
  // Python packages are now loaded and can be used
});

fs.readFile('./path/to/file.csv', 'utf8', (err, data) => {
  if (err) {
    console.error(err);
    return;
  }
  // The data variable contains the contents of the CSV file as a string
});
const dataFrame = pandas.read_csv(data);
