import logo from './logo.svg';
import './App.css';
import React, { Suspense, useMemo } from 'react';
import About from './component/About';
import * as THREE from 'three';
import { Canvas, useLoader } from 'react-three-fiber';
import circleImg from './assets/circle.png'


function Points() {
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  let positions = useMemo(() => {
    let positions = [1,1,1,1,1,1]
  })
  return(
    <points>
      <bufferGeometry attach={"geometry"}>
        <bufferAttribute
          attach={['atributes', 'position']}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial 
        attach={"material"} 
        map={imgTex}
        color={0x00AAFF}
        size={0.5}
        sizeAttenuation
        transparent={false}
        alphaTest={0.5}
        opacity={1}
        />
    </points>
  );
}
function AnimationCanvas() {
  return (
    <Canvas
      colorManagment={false}
      camera={{ position: [100, 10, 0], fov: 75 }}
    >
      <Suspense fallback={null}>
        <Points />
      </Suspense>

    </Canvas>

  );
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Suspense fallback={<div>Loading...</div>}>
          <AnimationCanvas />
        </Suspense>

        <img src={logo} className="App-logo" alt="logo" />
        <img src={logo} className="App-logo-animation" alt="logo" />
        <p>
          Plataforma Open-source Online de Visualización Tridimensional Multivariable de Bases de Datos Científicos
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Visualizar Bases de Datos
        </a>
        <div style={{ textAlign: "center" }}>
          <h1>REACTJS CSV IMPORT EXAMPLE </h1>
          <form>
            <input type={"file"} accept={".csv"} />
            <button>IMPORT CSV</button>

          </form>
        </div>
        <div><About /></div>
      </header>

    </div>
  );
};
export default App;
